INSERT INTO `guzi_discord_message` VALUES (69, 1368310814544494732, 26, '📢┃announcements', '1350994593642053734', 'Tectra', '9ef81bc96295077f55774dc2fa497f03', '', '1353767430903693473', 'You think most of them are bots because there is so much participation, but they are not. \n\nI understand your concern, I\'m ending the event for now. We will add the remaining blendlist quota to Kareoke night and other events on discord. \n\nand don\'t worry. We will keep an eye on Discord activities and we will check everything one by one. Not a single bot will get a Blendlist. \n\nWe will publish the OAT for the FCFS stage in a moment.\n\n@everyone', '', '1746301120', '0', 'https://discord.com/channels/1350994593642053734/1353767430903693473', 1, 1747560120, 1747562569);
INSERT INTO `guzi_discord_message` VALUES (70, 1372945474218233876, 26, '📣｜announcements', '1354818163879182519', 'Optimum', '4062f96b33f806b7a012076b4c5ace44', 'Optimum is the world\'s first high-performance memory infrastructure for any blockchain.', '1356459117362807004', '**## Muriel Médard Coindesk Live Interview Today** 🎙️♾️\nCatch **Muriel Médard**, Co-Founder & CEO of Optimum, in a **live interview with Coindesk** later today — happening just hours after her mainstage presentation at Consensus 2025.\n\n🗓️ **Date**: Friday, May 16\n🕞 **Interview Time**: 3:35 PM EDT\n📺 **Watch live**: https://www.youtube.com/watch?v=ikeWMoTgkIc\n\n🔁 **Quick reminder to watch her mainstage presentation:**\n\n🧠 **Talk**: The World Computer’s Memory\n📍 **Location**: Mainstage\n🕥 **Time**: 10:45 AM – 11:00 AM EDT\n🔗 **Livestream**:\nhttps://consensus2025.coindesk.com/agenda/event/-the-world-computers-memory-117\n\n🎟️ Register for a free virtual pass to watch. @everyone', '', '1747406109', '0', 'https://discord.com/channels/1354818163879182519/1356459117362807004', 1, 1747560121, 1747660154);
INSERT INTO `guzi_discord_message` VALUES (71, 1372931832340811897, 26, '📣｜announcements', '1354818163879182519', 'Optimum', '4062f96b33f806b7a012076b4c5ace44', 'Optimum is the world\'s first high-performance memory infrastructure for any blockchain.', '1356459117362807004', '## Still Time to Enter the Battle of the Codes Contest!\nOur **Community Contest** is live and running — but not for long!\n**Deadline to submit is Wednesday, May 21 🗓️**\nIf you’ve been meaning to jump in but need a little inspiration, check out two standout submissions that are raising the bar so far 👇\n\n🔥 **SIR DREY** delivered a [thread + blog post combo](https://x.com/_Dreyville/status/1921636240463421899) that breaks it all down with clarity.\nSmart, digestible, and sharp — a top-tier entry 🤌\n\n\n🤣 **Monkey Boy** followed it up with a [meme-packed thread](https://x.com/ApeBoy4Life/status/1920845246885249495) that’s both educational and hilarious.\n\nThis is your chance to show off what you’ve learned about **RLNC vs Reed-Solomon & Fountain Codes**, and get your work spotlighted in the community.\n\n📝 Submit your entry here: https://tally.so/r/mOgJeR \n 📲 And don’t forget to reply under the [contest X post](https://x.com/get_optimum/status/1920253951502659672)!\n\nLet’s finish strong. ♾️ @everyone', '', '1747402856', '1747402887', 'https://discord.com/channels/1354818163879182519/1356459117362807004', 1, 1747560121, 1747660154);
INSERT INTO `guzi_discord_message` VALUES (72, 1372338404934553631, 26, '📣｜announcements', '1354818163879182519', 'Optimum', '4062f96b33f806b7a012076b4c5ace44', 'Optimum is the world\'s first high-performance memory infrastructure for any blockchain.', '1356459117362807004', '**## Watch Live: Muriel Médard at Consensus 2025 📢**\nMuriel Médard, Co-Founder & CEO of Optimum, will be speaking live at Consensus 2025 this Friday!\n\n**🎙️ Talk:** The World Computer’s Memory\n**📍 Location:** Mainstage\n**🗓️ Date:** Friday, May 16\n**🕥 Time:** 10:45 AM – 11:00 AM EDT (15 minutes)\n\n**🔗 Watch the livestream here:**\nhttps://consensus2025.coindesk.com/agenda/event/-the-world-computers-memory-117\n\nRegister for a free virtual pass!\n\nDon’t miss it! ♾️ @everyone', '', '1747261372', '1747404932', 'https://discord.com/channels/1354818163879182519/1356459117362807004', 1, 1747560121, 1747660154);
INSERT INTO `guzi_discord_message` VALUES (73, 1369814079459426436, 26, '📣｜announcements', '1354818163879182519', 'Optimum', '4062f96b33f806b7a012076b4c5ace44', 'Optimum is the world\'s first high-performance memory infrastructure for any blockchain.', '1356459117362807004', '**## Battle of the Codes Community Contest Starts Now ♾️🥽**\nWe’re launching a creative contest to help spread the message behind one of the most important ideas powering Optimum:\n\nWhy Random Linear Network Coding (RLNC) outperforms Reed-Solomon and Fountain Codes — and what that means for the future of blockchains.\n\n**Your mission?**\nCreate content that captures this message and helps others understand it. Format is up to you. Be technical, be funny, be bold — just make it Optimum-quality.\n\n**A few ideas to get you started:**\n🧵 Twitter Thread summarizing key takeaways from the paper\n\n📊 Infographic showing how RLNC compares to Reed-Solomon and Fountain Codes\n\n📺 Short video or animation highlighting the network scalability benefit of RLNC\n\n🖼️ Meme that brings the RLNC vs RS/Fountain contrast to life\n\n✍️ Article or blog post on why erasure codes matter in blockchain infra\n\n**Get creative!** The winning idea may not be listed above.\n\n**💡 Content You Can Pull From**\nUse the following to guide and inform your piece:\n\n📄 Blog Post: [“Battle of the Codes: RLNC vs Reed-Solomon & Fountain Codes”](https://t.co/chb4zPkROV)\n\n🧵 X Thread: [Muriel\'s Thread on Network Coding](https://x.com/MurielMedard/status/1919761132480561270)\n\n🎧 Twitter Space: [Battle of the Codes X space](https://x.com/i/spaces/1zqJVjWPpZlJB)\n\n📺 Video: [Muriel’s introduction of erasure correcting code](https://x.com/MurielMedard/status/1920176385953251716)\n\n**📅 Timeline**\nContest Opens: Wednesday, May 7\n\nSubmit By: Wednesday, May 21\n\nSubmit using this **form**: [Submission Form Link](https://t.co/RSgsdLPkne)\n\nAlso post your submission as a **QT reply** under the [contest X post](https://x.com/get_optimum/status/1920253951502659672)\n\nWinners Announced: Wednesday, May 28\n\n**✨ Rewards**\nSelected submissions will be highlighted in our new X-Highlights Discord channel\n\nStandouts may be considered for future special positions within the community\n\nLet’s see what you’ve got. ♾️⚡ @everyone', '', '1746659526', '1746659730', 'https://discord.com/channels/1354818163879182519/1356459117362807004', 1, 1747560121, 1747660154);
