INSERT INTO `guzi_discord_message` VALUES (98, 1351200698255282238, 27, '📣・builders-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1258353044270219317', '# Reminder: Ideathon 3 is happening tomorrow! 🚀\n\nOn **March 18th at 7:00 AM MST / 10:00 PM SGT**, we’re diving into the EARN track as part of the EDU Chain Semester 3 Hackathon.\n\nJoin us as Elton from Second Talent, Komei from Animoca Brands, Shan and Jake from Open Campus, and Harry from HackQuest break down the challenges and opportunities in the EARN space. \n\nThis is the perfect chance to get insights into building impactful projects that shape the future of Web3 education.\n\n👉 Set your reminders: https://x.com/i/spaces/1lPKqMjEZmNKb\n<@&1153774282955046962>', '', '1742221750', '0', 'https://discord.com/channels/1088034981814738995/1258353044270219317', 1, 1747660553, 0);
INSERT INTO `guzi_discord_message` VALUES (99, 1350134703818276914, 27, '📣・builders-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1258353044270219317', '# <:CapyFriends800x80024:1296126909784002623> Launch Your dApp Before March 24 for Yuzu Rewards\n\n> dApps that go live before **March 24 **will be eligible for Yuzu Points in the upcoming season—those launching later will have to wait until Season 3.\n> 🔗 *[If your dApp went live in Season 1, submit the form to be included in the dApp Yuzu allocation.](https://airtable.com/appJ0riGGgpw9EvDG/pagwPXDAg2OlC2JDI/form)*', '', '1741967597', '0', 'https://discord.com/channels/1088034981814738995/1258353044270219317', 1, 1747660553, 0);
INSERT INTO `guzi_discord_message` VALUES (100, 1349011943251251251, 27, '📣・builders-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1258353044270219317', '# **🚀 LEVEL UP YOUR HACKATHON WITH AN OC MENTOR! 🧑‍🏫 **\n\nHackathons are better with guidance 🛠️\n\nThis semester, we’re pairing up builders with OC mentors - previous hackathon winners, top contributors, and Web3 pros ready to share their insights! 💡\n\nWith live office hours, AMAs, and Q&A threads, your mentor will help you stay on track, refine ideas and ensure you feel supported throughout the process. \n\nWant in? Register your interest now! Sign up using the link https://bit.ly/EDUMentee\n\n<:OCheart:1144622879892328540>\n\n@everyone', '', '1741699910', '1741699946', 'https://discord.com/channels/1088034981814738995/1258353044270219317', 1, 1747660553, 0);
INSERT INTO `guzi_discord_message` VALUES (101, 1373959833216090142, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '**A contest. A showdown. A bear to beat.\nAnd $1000 in $EDU up for grabs.**\n\nNext week, we’re opening the gates to the Eddy Shill Tank.\nWhere your pitch could be your payday.\n→ Got a memecoin you swear by?\n→ Got the charisma to make Eddy believe?\n→ Got 60 seconds to record a legendary shill?\nStart sharpening your takes.\nLimited spots.\nThis is not a drill.\nThis is Eddy’s beta brawl: test yourself, test the bear, and help shape the next wave of competitions.\n\nLaunch this challenge with @‌four_meme_, let’s see those @‌BNBChain memecoins fly\n\nThis campaign is not officially associated with any tokens. Beware of scams and suspicious links, always refer to our official channels when in doubt.\n\n<@&1153774282955046962>', '', '1747647951', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747660801, 1747804820);
INSERT INTO `guzi_discord_message` VALUES (102, 1372938024052330586, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '# **OC Weekly Roundup #46 is here!**\n\nFrom Core Contributors shaping the DAO to $EDU’s bold move into the $5T education market, OC is turning heads and breaking ground.\n\nDcodeBlock’s got 1.26M Yuzu in bloom, GainzSwap is now tracked on DefiLlama, and SailFish is stacking rewards for the boldest traders.\n\nSkim the drops, meet the builders, and get in on the action. ⤵️\n👉 Follow [@OpenCampus_xyz](https://x.com/opencampus_xyz) for weekly drops.\n\n👉 Read the [full roundup on X.](https://x.com/opencampus_xyz/status/1923285017939280018)\n\n<@&1153774282955046962>', '', '1747404332', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747660801, 1747804820);
INSERT INTO `guzi_discord_message` VALUES (103, 1372228642666451064, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '📍 **Pinned It. GEOGuessr winners!** 🌍\n\nCongrats to our top GEOGuessr champs:\n🥇 <@789250184734441522> – 10,000 Yuzu\n🥈 <@344065002669604865> – 5,000 Yuzu\n🥉 <@1049436004014108702> – 2,500 Yuzu\n\nOpen a <#1088034983014301733>. Send us your wallet address. Claim your Yuzu! 💥\n\nKeep exploring! 🗺️\n\n<@&1153774282955046962>', '', '1747235203', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747660801, 1747804820);
INSERT INTO `guzi_discord_message` VALUES (104, 1370994400708263957, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '🚨SECURITY UPDATE FROM OPEN CAMPUS TEAM 🚨\n\nHi everyone,\n\nEarlier today around 11am HKT, one of our moderators had their account compromised and began posting malicious links. We are actively investigating how this breach occurred and hope none of you were impacted.\n\nThanks to our vigilant community, we were able to respond quickly and have taken precautionary measures to secure our Discord and other channels. Safety and security are our top priorities, so if you have any questions or concerns, please raise a support ticket.\n\nIn the meantime, we highly recommend checking out our security education partner, Boring Security, for workshops and resources to help you stay protected.\n\nThank you and stay safe.', '', '1746940936', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747660801, 1747746856);
INSERT INTO `guzi_discord_message` VALUES (105, 1369585817512837232, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '# Yuzu Mid Season Booster 🍊\n\nWe have awarded an extra 15,000,000 Yuzu to EDU Chain dApps for their strong performance in the first month of Season 2. \n\nWe measured performance using TVL, unique active wallets (UAWs), and transaction counts, based on a snapshot taken on April 27th. \n\nThe next snapshot will be on May 27th, with additional Yuzu to be distributed based on results from the month. \n\nFor a full breakdown, please read the [X article](https://x.com/educhain_xyz/status/1920023926098346227)\n\nTo start farming Yuzu from the dApps, head to our [ecosystem page](https://educhain.xyz/ecosystem) and start exploring.\n\n<@&1153774282955046962> <@&1289131607860707409>', '', '1746605104', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747660801, 1747663787);
INSERT INTO `guzi_discord_message` VALUES (106, 1374035178371944468, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '# Weekly \"Tuesday Tuition\" Session <:CapyFriends800x80024:1296126909784002623> \n\nWe\'ve got a community session coming up, hosted by Capy <@854130943982567466>  and featuring builders from our community 👇\n\n*Time: 2 PM UTC\nDate: May 20th, 2025\nLocation: Open Campus Discord\nGuests: MoveFlow and EduHub\nTopic: Education and AI\n\nThis week’s session is built for builders and users who care about real traction in EduFi. We’ll explore what our featured dApps actually deliver, how users can get started, and which parts of the education and finance stack they’re targeting.\n\nAgenda\n• Introduce the week’s topic and featured dApp teams\n• Break down what each product does and how users can engage\n• Explore how each dApp addresses a specific EduFi challenge\n• Open discussion with the community\n\nWe’ll be asking sharp questions around product access, user flow, and outcomes. The goal is clarity, connection, and action. \n\nJoin us live and get in the loop: https://discord.com/events/1088034981814738995/1373306220781244536*\n\n<@&1153774282955046962>', '', '1747665914', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747667431, 1747804820);
INSERT INTO `guzi_discord_message` VALUES (107, 1374069348389621833, 26, '📢┃announcements', '1350994593642053734', 'Tectra', '9ef81bc96295077f55774dc2fa497f03', '', '1353767430903693473', 'https://x.com/TectraNFT/status/1924510045678534760 @everyone', '', '1747674061', '0', 'https://discord.com/channels/1350994593642053734/1353767430903693473', 1, 1747690931, 1747804822);
INSERT INTO `guzi_discord_message` VALUES (108, 1374422804576862288, 27, '📣・project-updates', '1088034981814738995', 'Open Campus', '2ace41d8418c2de3e8b7ebb1abf5bd77', 'A community-led DAO bringing education on-chain.', '1088038320916930570', '# 📢 **YUZU SERVER BOOST REWARDS UPDATE** 📢\n\nWow—we’re truly amazed by the incredible response to the server boost campaign! So many more of you boosted the server than we ever expected. Thank you for showing such strong support for Open Campus.\n\nWith so many boosters, the total Yuzu rewards would have exceeded even what some dApps received for their Season 2 allocation. To keep things sustainable and fair for everyone, we had to adjust our approach:\n\n**All boosters were entered into a raffle, and 10 lucky winners each received 10,000 Yuzu.**\n\nCongratulations to:\n\n<@1097210761874514020> \n<@1097211745086484500> \n<@1351211163123449938> \n<@660108209926701076> \n<@1351211122648420553> \n<@696702918749061120> \n<@1363793480261963848> \n<@1346099394721743010> \n<@1068869015553986571> \n<@1351217949549133956> \n\nWe know some of you may be disappointed, and we truly appreciate your understanding.\n\nWinners of this Yuzu giveaway, along with other outstanding Yuzu giveaways, can claim their Yuzu by the end of the week.\n\nAppreciate the support, as always 🙏\n\n<@&1153774459912724639>', '', '1747758332', '0', 'https://discord.com/channels/1088034981814738995/1088038320916930570', 1, 1747781063, 1747804820);
